import React, { Component } from 'react';
import './User.css';
import { Button, Form, FormGroup, Label, Input, Col } from 'reactstrap';

class AddUser extends Component {

    constructor(props) {
        super(props);
        let currentUser = props.currentUser || {}
        const labels = {
            'addUser': 'Add User',
            'editUser': 'Update User'
        };
        this.state = {
            id: currentUser.id || '',
            firstName: currentUser.firstName || '',
            lastName: currentUser.lastName || '',
            employeeID: currentUser.employeeID || '',
            addUserResponse: props.addUserResponse || {},
            userAction: props.userAction || 'addUser',
            labels: labels
        }
    }

    componentWillReceiveProps(nextProps) {
        console.log("Next set of props")
        console.log(nextProps)
        if (nextProps.addUserResponse && nextProps.addUserResponse.status == "success") {
            // Show success message?
            this.reset();
        }

        if (nextProps.addUserResponse && nextProps.addUserResponse.status == "failure") {
            // Show failure message
        }
        let currentUser = nextProps.currentUser || {}
        this.setState({
            id: currentUser.id || '',
            firstName: currentUser.firstName || '',
            lastName: currentUser.lastName || '',
            employeeID: currentUser.employeeID || '',
            userAction: nextProps.userAction || 'addUser',
        });
    }

    render() {
        return (
            <div className="form-section">
                <Form>
                    <FormGroup row>
                        <Label for="firstName" sm={2}>First Name:</Label>
                        <Col sm={10}>
                            <Input type="text" value={this.state.firstName} onChange={e => this.handleChange("firstName", e)} />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="lastName" sm={2}>Last Name:</Label>
                        <Col sm={10}>
                            <Input type="text" value={this.state.lastName} onChange={e => this.handleChange("lastName", e)} />
                        </Col>
                    </FormGroup>
                    <FormGroup row>
                        <Label for="employeeID" sm={2}>Employee ID:</Label>
                        <Col sm={6}>
                            <Input type="text" value={this.state.employeeID} onChange={e => this.handleChange("employeeID", e)} />
                        </Col>
                    </FormGroup>

                    <FormGroup check row>
                        <Col sm={{ size: 10, offset: 6 }}>
                            <Button onClick={e => this.addOrEditUser(e, this.state.userAction)}>{this.state.labels[this.state.userAction]}</Button>
                            <Button className="secondary" onClick={e => this.reset(e)}>Reset</Button>
                        </Col>
                    </FormGroup>
                </Form>
            </div>
        );
    }

    addOrEditUser(e, action) {
        // validate user
        const user = {
            id: this.state.id,
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            employeeID: this.state.employeeID
        };
        if (action === 'addUser') {
            user.id = 'userid-' + Math.random().toString(36).substr(2, 16);
        }
        console.log(user);
        this.props.addOrEditUser(action, user);
    }

    reset(e) {
        this.setState({
            id: '',
            firstName: '',
            lastName: '',
            employeeID: ''
        })
    }

    handleChange(name, e) {
        this.setState({
            [name]: e.target.value
        });
    }

}

export default AddUser;
