import React, {
  Component
} from 'react';
import logo from './assets/images/cognizant_logo.svg';

import './App.css';
import User from './assets/components/user/User';
import Project from './assets/components/project/Project';
import ViewTask from './assets/components/task/ViewTask';
import AddTask from './assets/components/task/AddTask';
import { Navbar, NavbarBrand, TabContent, TabPane, Nav, NavItem, NavLink, Row, Col } from 'reactstrap';
import classnames from 'classnames';

class App extends Component {

  constructor(props) {
    super(props);
    let defaultuserList = [{
      firstName: 'KJ',
      lastName: 'JK',
      employeeID: '3002248'
    }, {
      firstName: 'KJ-1',
      lastName: 'JK-1',
      employeeID: '3002249'
    }]
    let defaultTaskList = [{
      task: 'validatelogin',
      parent: 'login',
      priority: '6',
      start:"12-12-2019",
      end:"12-13-2018",
      project:"PMT"

    }, {
      task: 'errorlogin',
      parent: 'login',
      priority: '4',
      start:"12-12-2018",
      end:"12-14-2018",
      project:"PMT"
    }]
    this.state = {
      activeTab: '1',
      userList: props.userList || defaultuserList,
      projectsList: props.projectsList || [],
      taskList: props.taskList || defaultTaskList
    };
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  render() {
    return (
      <div>
        <div className="app-branding">
          <img srcSet={logo} width="200" height="40" />
        </div>
        <Navbar color="light" light expand="md" sticky="top">
          <NavbarBrand href="/" >Project Manager App</NavbarBrand>
        </Navbar>
        <div className="app-container">
          <Nav tabs>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '1' })}
                onClick={() => { this.toggle('1'); }}
              >
                Add Project
            </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '2' })}
                onClick={() => { this.toggle('2'); }}
              >
                Add Task
            </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '3' })}
                onClick={() => { this.toggle('3'); }}
              >
                Add User
            </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: this.state.activeTab === '4' })}
                onClick={() => { this.toggle('4'); }}
              >
                View Task
            </NavLink>
            </NavItem>
          </Nav>
          <TabContent activeTab={this.state.activeTab}>
            <TabPane tabId="1">
              <Row>
                <Col sm="12">
                  <Project userList={this.state.userList} projectsList={this.state.projectsList} />
                </Col>
              </Row>
            </TabPane>
            <TabPane tabId="2">
              <Row>
                <Col sm="12">
                  <AddTask userList={this.state.userList} projectsList={this.state.projectsList} taskList={this.state.taskList}
                    addOrEditTask={(userAction, task) => this.addOrEditTask(userAction, task)} />
                </Col>
              </Row>
            </TabPane>
            <TabPane tabId="3">
              <Row>
                <Col sm="12">
                  <User userList={this.state.userList} />
                </Col>
              </Row>
            </TabPane>
            <TabPane tabId="4">
              <Row>
                <Col sm="12">
                  <ViewTask taskList={this.state.taskList}/>
                </Col>
              </Row>
            </TabPane>
          </TabContent>
        </div>
      </div>
    );
  }

  addOrEditTask(userAction, task) {
    let taskList = this.state.taskList;
    if (userAction === 'addTask') {
      taskList.push(task);
    } else if (userAction === 'editTask') {
      taskList.map(cTask => {
        if (cTask.id === task.id) {
          cTask.projectName = task.projectName;
          cTask.taskName = task.taskName;
          cTask.priority = task.priority;
          cTask.isParentTask = task.isParentTask;
          cTask.parentTask = task.parentTask;
          cTask.startDate = task.startDate;
          cTask.endDate = task.endDate;
          cTask.user = task.user;
        }
        return cTask
      });
    }
    this.setState({
      taskList: taskList
    });
  }

}

export default App;
